import { Component } from '@angular/core';
import { VideoDetails } from 'src/youtube-response.model';
import { YoutubeService } from './youtube.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  videos: VideoDetails[] = [];
  prevPageToken: string = '';
  nextPageToken: string = '';

  constructor(private youtubeService: YoutubeService){}

  ngOnInit(){
    this.loadVideos();
  }

  loadVideos(pageToken: string | undefined = '')
  {
    this.youtubeService.getChannelVideos(pageToken, 12).subscribe(response => {
      this.videos = response.video;
      this.prevPageToken = response.prevPageToken;
      this.nextPageToken = response.nextPageToken;
    })
  }
}
<!DOCTYPE html>
<html>
  <head>
    <!-- The head section includes meta tags, title, and any external styles or scripts -->
    <meta charset="UTF-8">
    <title>Your Page Title</title>
    <link
      href="https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css"
      rel="stylesheet"
    />
    <!-- Additional meta tags, styles, or scripts go here -->
    <style>
      /* Custom Button Styles */
      /* 15 */
      .custom-btn.btn-15 {
        background: #b621fe;
        color: #fff;
        border: none;
        z-index: 1;
      }
      .custom-btn.btn-15:after {
        position: absolute;
        content: "";
        width: 0;
        height: 100%;
        top: 0;
        right: 0;
        z-index: -1;
        background-color: #663dff;
        border-radius: 5px;
        box-shadow: inset 2px 2px 2px 0px rgba(255, 255, 255, 0.5),
          7px 7px 20px 0px rgba(0, 0, 0, 0.1),
          4px 4px 5px 0px rgba(0, 0, 0, 0.1);
        transition: all 0.3s ease;
      }
      .custom-btn.btn-15:hover:after {
        left: 0;
        width: 100%;
      }
      .custom-btn.btn-15:active {
        top: 2px;
      }

      /* 16 */
      .custom-btn.btn-16 {
        border: none;
        color: #000;
        z-index: 1;
      }
      .custom-btn.btn-16:after {
        position: absolute;
        content: "";
        width: 0;
        height: 100%;
        top: 0;
        left: 0;
        direction: rtl;
        z-index: -1;
        box-shadow: -7px -7px 20px 0px #fff9, -4px -4px 5px 0px #fff9,
          7px 7px 20px 0px #0002,
          4px 4px 5px 0px #0001;
        transition: all 0.3s ease;
      }
      .custom-btn.btn-16:hover:after {
        left: auto;
        right: 0;
        width: 100%;
      }
      .custom-btn.btn-16:active {
        top: 2px;
      }
    </style>
  </head>
  <body class="bg-black">
   <!-- Section 1: Title -->
   <div class="h-screen flex items-center justify-center">
    <div class="text-center">
      <h1 class="text-4xl font-bold mb-8 text-white">
        Dotnet 8 and YouTube Dev API
      </h1>
    </div>
  </div>

   <!-- Section 2: Videos -->
   <div class="container mx-auto text-center mt-4">
    <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
    <div *ngFor="let video of videos" class="bg-gray-900 rounded-lg p-4 hover:bg-gray-800 transition duration-300">
      <h2 class="text-xl font-semibold mb-2 text-white">{{ video.title }}</h2>
      <p class="text-gray-400 text-white">
        Published on: {{ video.publishedAt | date:'short' }}
      </p>
      <a [href]="video.link" target="_blank">
        <img
          [src]="video.thumbnail"
          alt="{{ video.title }}"
          class="mt-4 rounded-lg"
        />
      </a>
    </div>
  </div>
</div>


<!-- Section 3: Pagination Controls -->
<div class="mt-4 text-center">
  <div>
    <button class="custom-btn btn-15" onclick="loadVideos('prevPageToken')">Previous Page</button>
    <button class="custom-btn btn-16" onclick="loadVideos('nextPageToken')">Next Page</button>
  </div>
</div>

  </body>
</html>
