import { Component } from '@angular/core';
import { VideoDetails } from 'src/youtube-response.model';
import { YoutubeService } from './youtube.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  videos: VideoDetails[] = [];
  prevPageToken: string = '';
  nextPageToken: string = '';

  constructor(private youtubeService: YoutubeService){}

  ngOnInit(){
    this.loadVideos();
  }

  loadVideos(pageToken: string | undefined = '')
  {
    this.youtubeService.getChannelVideos(pageToken, 10).subscribe(response => {
      this.videos = response.video;
      this.prevPageToken = response.prevPageToken;
      this.nextPageToken = response.nextPageToken;
    })
  }
}
<!DOCTYPE html>
<html>
  <head>
    <!-- The head section includes meta tags, title, and any external styles or scripts -->
    <meta charset="UTF-8">
    <title>Your Page Title</title>
    <!-- Additional meta tags, styles, or scripts go here -->
  </head>
  <body>
    <div class="container">
      <h1>Dotnet 8 and Youtube Dev Api</h1>

      <!-- Display videos -->
      <div class="video-list">
        <div *ngFor="let video of videos" class="video">
          <h2>{{ video.title }}</h2>
          <p>Published on: {{ video.publishedAt | date:'short' }}</p>
          <a [href]="video.link" target="_blank">
            <img [src]="video.thumbnail" alt="{{ video.title }}" />
          </a>
        </div>
      </div>

      <!-- Pagination controls -->
      <div class="pagination">
        <button *ngIf="prevPageToken" (click)="loadVideos(prevPageToken)">Previous Page</button>
        <button *ngIf="nextPageToken" (click)="loadVideos(nextPageToken)">Next Page</button>
      </div>
    </div>
  </body>
</html>
