import { Component } from '@angular/core';
import { VideoDetails } from 'src/youtube-response.model';
import { YoutubeService } from './youtube.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  videos: VideoDetails[] = [];
  prevPageToken: string = '';
  nextPageToken: string = '';

  constructor(private youtubeService: YoutubeService){}

  ngOnInit(){
    this.loadVideos();
  }

  loadVideos(pageToken: string | undefined = '')
  {
    this.youtubeService.getChannelVideos(pageToken, 10).subscribe(response => {
      this.videos = response.video;
      this.prevPageToken = response.prevPageToken;
      this.nextPageToken = response.nextPageToken;
    })
  }
}
<!DOCTYPE html>
<html>
  <head>
    <!-- The head section includes meta tags, title, and any external styles or scripts -->
    <meta charset="UTF-8">
    <title>Your Page Title</title>
    <link href="https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css" rel="stylesheet">
    <!-- Additional meta tags, styles, or scripts go here -->
  </head>
  <body class="bg-black h-screen flex items-center justify-center">
    <div class="container mx-auto text-center">
      <h1 class="text-4xl font-bold mb-8">Dotnet 8 and YouTube Dev API</h1>

      <!-- Display videos -->
      <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        <div *ngFor="let video of videos" class="bg-gray-800 rounded-lg p-4">
          <h2 class="text-xl font-semibold mb-2">{{ video.title }}</h2>
          <p class="text-gray-400">Published on: {{ video.publishedAt | date:'short' }}</p>
          <a [href]="video.link" target="_blank">
            <img [src]="video.thumbnail" alt="{{ video.title }}" class="mt-4 rounded-lg" />
          </a>
        </div>
      </div>

      <!-- Pagination controls -->
      <div class="mt-8">
        <button *ngIf="prevPageToken" class="px-4 py-2 bg-blue-500 text-white rounded-md mr-4" (click)="loadVideos(prevPageToken)">Previous Page</button>
        <button *ngIf="nextPageToken" class="px-4 py-2 bg-blue-500 text-white rounded-md" (click)="loadVideos(nextPageToken)">Next Page</button>
      </div>
    </div>
  </body>
</html>
